SELECT count(h.fileID)as FilesUploaded,h.stationID, station.callSign, station.communityState, station.nielsenDMA FROM `bigquery-public-data.fcc_political_ads.file_record` JOIN `bigquery-public-data.fcc_political_ads.broadcast_tv_radio_station` as station USING (stationID) WHERE YEAR = 2016 Group by h.stationID, station.callSign, station.communityState, station.nielsenDMA ORDER BY count(fileID) desc
