SELECT sub_region_3_code AS zip_code, city, sub_region_2 AS county, sub_region_1 AS state, AVG(sni_safety_side_effects) AS safety_side_effects FROM `bigquery-public-data.covid19_vaccination_search_insights.covid19_vaccination_search_insights` vaccine JOIN `bigquery-public-data.geo_us_boundaries.zip_codes` zips ON vaccine.sub_region_3_code = zips.zip_code WHERE  sub_region_1 = "California" AND date < "2021-03-01" AND date > "2020-12-31" GROUP BY zip_code, city, county, state ORDER BY safety_side_effects
